import React from 'react' // мы обязаны импортировать необходимые пакеты в каждом файле
import PropTypes from 'prop-types' // у Article это react и prop-types”
import styled, { css } from "styled-components";

class Article extends React.Component {
    state = {
        visible:false
    }
    moreHandler = (e) => {
        e.preventDefault()
        this.setState({visible:!this.state.visible})
    }
    render() {
        const {author,text,bigText} = this.props.data
        const {visible} = this.state
        return(
            <div className="news-article">
            <p>Author: {author}</p>
        <p>Text {text}</p>
        {
            !visible && <a onClick={this.moreHandler} href="#">more</a>}
            { visible && <p>{bigText}</p>
            }
            {visible && <a onClick={this.moreHandler} href="#">less</a>}


                </div>
            )
    }
}
Article.propTypes = {
    data: PropTypes.shape ({
        author: PropTypes.string.isRequired,
        text: PropTypes.string.isRequired,
    })
}
export {Article}
